ovisionedPackage -Online | Where-Object DisplayName -EQ $app | Remove-AppxProvisionedPackage -Online}#Get-AppxPackage -AllUsers | % { if($_.Name -in $apps){ Remove-AppxPackage -AllUsers } }#Get-AppxProvisionedPackage -Online | % { if($_.DisplayName -in $apps){ Remove-AppxProvisionedPackage -Online } } # Prevents "Suggested Applications" returningforce-mkdir "HKLM:\SOFTWARE\Policies\Microsoft\Windows\Cloud Content"Set-ItemProperty "HKLM:\SOFTWARE\Policies\Microsoft\Windows\Cloud Content" "DisableWindowsConsumerFeatures" 1
